SELECT On_Time_On_Time_Performance_2016_1.Flights, L_AIRPORT_SEQ_ID.Code, MAX(On_Time_On_Time_Performance_2016_1.OriginAirportID) AS max_On_Time_On_Time_Performance_2016_1_OriginAirportID, MAX(On_Time_On_Time_Performance_2016_1.ArrDel15) AS max_On_Time_On_Time_Performance_2016_1_ArrDel15, COUNT(On_Time_On_Time_Performance_2016_1.Year) AS count_On_Time_On_Time_Performance_2016_1_Year, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Div1AirportSeqID WHERE L_AIRPORT_SEQ_ID.Code IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.Flights, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 9