SELECT On_Time_On_Time_Performance_2016_1.DestStateFips, L_AIRPORT_SEQ_ID.Code, MAX(On_Time_On_Time_Performance_2016_1.DepDel15) AS max_On_Time_On_Time_Performance_2016_1_DepDel15, AVG(On_Time_On_Time_Performance_2016_1.DivArrDelay) AS avg_On_Time_On_Time_Performance_2016_1_DivArrDelay, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Div1AirportSeqID WHERE L_AIRPORT_SEQ_ID.Description LIKE '%' AND (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) AND (On_Time_On_Time_Performance_2016_1.Quarter < 10000000 OR On_Time_On_Time_Performance_2016_1.Quarter IS NULL) GROUP BY On_Time_On_Time_Performance_2016_1.DestStateFips, L_AIRPORT_SEQ_ID.Code