SELECT L_AIRPORT_SEQ_ID.Description, L_AIRPORT_SEQ_ID.Code, DENSE_RANK() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Code ORDER BY L_AIRPORT_SEQ_ID.Description) AS win_2, RANK() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Code ORDER BY L_AIRPORT_SEQ_ID.Description) AS win_3, AVG(L_AIRPORT_SEQ_ID.Code) OVER (PARTITION BY L_AIRPORT_SEQ_ID.Code ORDER BY L_AIRPORT_SEQ_ID.Description) AS win_4 FROM L_AIRPORT_SEQ_ID