SELECT On_Time_On_Time_Performance_2016_1.Div1WheelsOn, L_AIRPORT_SEQ_ID.Description, SUM(On_Time_On_Time_Performance_2016_1.Quarter) AS sum_On_Time_On_Time_Performance_2016_1_Quarter, COUNT(On_Time_On_Time_Performance_2016_1.Div2TotalGTime) AS count_On_Time_On_Time_Performance_2016_1_Div2TotalGTime, MAX(On_Time_On_Time_Performance_2016_1.ArrivalDelayGroups) AS max_On_Time_On_Time_Performance_2016_1_ArrivalDelayGroups, AVG(On_Time_On_Time_Performance_2016_1.ArrDel15) AS avg_On_Time_On_Time_Performance_2016_1_ArrDel15, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Dest WHERE LENGTH(L_AIRPORT_SEQ_ID.Description) > 0 GROUP BY On_Time_On_Time_Performance_2016_1.Div1WheelsOn, L_AIRPORT_SEQ_ID.Description