SELECT On_Time_On_Time_Performance_2016_1.DestStateName, L_AIRPORT_SEQ_ID.Description, AVG(On_Time_On_Time_Performance_2016_1.DivActualElapsedTime) AS avg_On_Time_On_Time_Performance_2016_1_DivActualElapsedTime, MIN(On_Time_On_Time_Performance_2016_1.Div2TotalGTime) AS min_On_Time_On_Time_Performance_2016_1_Div2TotalGTime, STDDEV(On_Time_On_Time_Performance_2016_1.Cancelled) AS stddev_On_Time_On_Time_Performance_2016_1_Cancelled, MAX(On_Time_On_Time_Performance_2016_1.DepTime) AS max_On_Time_On_Time_Performance_2016_1_DepTime, MAX(On_Time_On_Time_Performance_2016_1.DestCityMarketID) AS max_On_Time_On_Time_Performance_2016_1_DestCityMarketID, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID INNER JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.OriginCityMarketID WHERE LENGTH(L_AIRPORT_SEQ_ID.Description) > 0 GROUP BY On_Time_On_Time_Performance_2016_1.DestStateName, L_AIRPORT_SEQ_ID.Description