SELECT L_AIRPORT_SEQ_ID.Description, L_AIRPORT_SEQ_ID.Code, SUM(L_AIRPORT_SEQ_ID.Code) OVER (PARTITION BY L_AIRPORT_SEQ_ID.Description ORDER BY L_AIRPORT_SEQ_ID.Description) AS win_2, RANK() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Description ORDER BY L_AIRPORT_SEQ_ID.Code) AS win_3 FROM L_AIRPORT_SEQ_ID