SELECT On_Time_On_Time_Performance_2016_1.DepDelayMinutes, L_AIRPORT_SEQ_ID.Code, AVG(On_Time_On_Time_Performance_2016_1.DivReachedDest) AS avg_On_Time_On_Time_Performance_2016_1_DivReachedDest, SUM(On_Time_On_Time_Performance_2016_1.DistanceGroup) AS sum_On_Time_On_Time_Performance_2016_1_DistanceGroup, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DestStateFips = L_AIRPORT_SEQ_ID.Code WHERE On_Time_On_Time_Performance_2016_1.DepTime IS NOT NULL AND L_AIRPORT_SEQ_ID.Code != -999999 AND LENGTH(L_AIRPORT_SEQ_ID.Description) > 0 GROUP BY On_Time_On_Time_Performance_2016_1.DepDelayMinutes, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 8