SELECT On_Time_On_Time_Performance_2016_1.Carrier, L_AIRPORT_SEQ_ID.Description, SUM(On_Time_On_Time_Performance_2016_1.DestAirportSeqID) AS sum_On_Time_On_Time_Performance_2016_1_DestAirportSeqID, STDDEV(On_Time_On_Time_Performance_2016_1.ArrDelayMinutes) AS stddev_On_Time_On_Time_Performance_2016_1_ArrDelayMinutes, MIN(On_Time_On_Time_Performance_2016_1.AirlineID) AS min_On_Time_On_Time_Performance_2016_1_AirlineID, COUNT(On_Time_On_Time_Performance_2016_1.Flights) AS count_On_Time_On_Time_Performance_2016_1_Flights, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.DepartureDelayGroups WHERE L_AIRPORT_SEQ_ID.Description != '' AND On_Time_On_Time_Performance_2016_1.DivDistance IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.Carrier, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 44