SELECT L_AIRPORT_SEQ_ID.Description, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID WHERE L_AIRPORT_SEQ_ID.Code > 0 GROUP BY L_AIRPORT_SEQ_ID.Description