SELECT L_AIRPORT_SEQ_ID.Code, L_AIRPORT_SEQ_ID.Description, AVG(L_AIRPORT_SEQ_ID.Code) OVER (PARTITION BY L_AIRPORT_SEQ_ID.Code ORDER BY L_AIRPORT_SEQ_ID.Code) AS win_2, ROW_NUMBER() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Description ORDER BY L_AIRPORT_SEQ_ID.Description) AS win_3, RANK() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Code ORDER BY L_AIRPORT_SEQ_ID.Code) AS win_4, RANK() OVER (PARTITION BY L_AIRPORT_SEQ_ID.Code ORDER BY L_AIRPORT_SEQ_ID.Code) AS win_5 FROM L_AIRPORT_SEQ_ID WHERE L_AIRPORT_SEQ_ID.Description != ''