SELECT On_Time_On_Time_Performance_2016_1.DestCityMarketID, L_AIRPORT_SEQ_ID.Code, MAX(On_Time_On_Time_Performance_2016_1.NASDelay) AS max_On_Time_On_Time_Performance_2016_1_NASDelay, MIN(On_Time_On_Time_Performance_2016_1.Div2AirportSeqID) AS min_On_Time_On_Time_Performance_2016_1_Div2AirportSeqID, SUM(On_Time_On_Time_Performance_2016_1.LateAircraftDelay) AS sum_On_Time_On_Time_Performance_2016_1_LateAircraftDelay, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID INNER JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Cancelled WHERE On_Time_On_Time_Performance_2016_1.ArrDelayMinutes IS NOT NULL AND L_AIRPORT_SEQ_ID.Code != -999999 GROUP BY On_Time_On_Time_Performance_2016_1.DestCityMarketID, L_AIRPORT_SEQ_ID.Code