SELECT L_AIRPORT_SEQ_ID.Description, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID WHERE L_AIRPORT_SEQ_ID.Code != -999999 GROUP BY L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 42