SELECT On_Time_On_Time_Performance_2016_1.DepDelayMinutes, L_AIRPORT_SEQ_ID.Code, SUM(On_Time_On_Time_Performance_2016_1.SecurityDelay) AS sum_On_Time_On_Time_Performance_2016_1_SecurityDelay, SUM(On_Time_On_Time_Performance_2016_1.DayOfWeek) AS sum_On_Time_On_Time_Performance_2016_1_DayOfWeek, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Dest WHERE (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) GROUP BY On_Time_On_Time_Performance_2016_1.DepDelayMinutes, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 3