SELECT On_Time_On_Time_Performance_2016_1.OriginState, L_AIRPORT_SEQ_ID.Description, AVG(On_Time_On_Time_Performance_2016_1.CRSArrTime) AS avg_On_Time_On_Time_Performance_2016_1_CRSArrTime, SUM(On_Time_On_Time_Performance_2016_1.OriginWac) AS sum_On_Time_On_Time_Performance_2016_1_OriginWac, SUM(On_Time_On_Time_Performance_2016_1.Diverted) AS sum_On_Time_On_Time_Performance_2016_1_Diverted, STDDEV(On_Time_On_Time_Performance_2016_1.DepDelay) AS stddev_On_Time_On_Time_Performance_2016_1_DepDelay, MIN(On_Time_On_Time_Performance_2016_1.ArrDelayMinutes) AS min_On_Time_On_Time_Performance_2016_1_ArrDelayMinutes, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.Div1AirportID = L_AIRPORT_SEQ_ID.Code GROUP BY On_Time_On_Time_Performance_2016_1.OriginState, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 24