SELECT On_Time_On_Time_Performance_2016_1.CarrierDelay, L_AIRPORT_SEQ_ID.Description, SUM(On_Time_On_Time_Performance_2016_1.AirTime) AS sum_On_Time_On_Time_Performance_2016_1_AirTime, MIN(On_Time_On_Time_Performance_2016_1.OriginWac) AS min_On_Time_On_Time_Performance_2016_1_OriginWac, MIN(On_Time_On_Time_Performance_2016_1.DivReachedDest) AS min_On_Time_On_Time_Performance_2016_1_DivReachedDest, AVG(On_Time_On_Time_Performance_2016_1.WheelsOff) AS avg_On_Time_On_Time_Performance_2016_1_WheelsOff, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.OriginAirportSeqID = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Description != '' AND On_Time_On_Time_Performance_2016_1.DestAirportID IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.CarrierDelay, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 4