SELECT On_Time_On_Time_Performance_2016_1.DepDel15, L_AIRPORT_SEQ_ID.Description, MIN(On_Time_On_Time_Performance_2016_1.TaxiOut) AS min_On_Time_On_Time_Performance_2016_1_TaxiOut, COUNT(On_Time_On_Time_Performance_2016_1.FirstDepTime) AS count_On_Time_On_Time_Performance_2016_1_FirstDepTime, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.DayOfWeek WHERE L_AIRPORT_SEQ_ID.Code IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.DepDel15, L_AIRPORT_SEQ_ID.Description