SELECT On_Time_On_Time_Performance_2016_1.Div2WheelsOn, L_AIRPORT_SEQ_ID.Code, MAX(On_Time_On_Time_Performance_2016_1.DayOfWeek) AS max_On_Time_On_Time_Performance_2016_1_DayOfWeek, MIN(On_Time_On_Time_Performance_2016_1.OriginAirportID) AS min_On_Time_On_Time_Performance_2016_1_OriginAirportID, AVG(On_Time_On_Time_Performance_2016_1.Distance) AS avg_On_Time_On_Time_Performance_2016_1_Distance, SUM(On_Time_On_Time_Performance_2016_1.Div1AirportID) AS sum_On_Time_On_Time_Performance_2016_1_Div1AirportID, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.AirlineID = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Code > 0 GROUP BY On_Time_On_Time_Performance_2016_1.Div2WheelsOn, L_AIRPORT_SEQ_ID.Code HAVING COUNT(*) > 6