SELECT On_Time_On_Time_Performance_2016_1.Div1TailNum, On_Time_On_Time_Performance_2016_1.OriginCityName, L_AIRPORT_SEQ_ID.Description, COUNT(On_Time_On_Time_Performance_2016_1.CRSDepTime) AS count_On_Time_On_Time_Performance_2016_1_CRSDepTime, MIN(On_Time_On_Time_Performance_2016_1.DivActualElapsedTime) AS min_On_Time_On_Time_Performance_2016_1_DivActualElapsedTime, SUM(On_Time_On_Time_Performance_2016_1.OriginWac) AS sum_On_Time_On_Time_Performance_2016_1_OriginWac, AVG(On_Time_On_Time_Performance_2016_1.DepDel15) AS avg_On_Time_On_Time_Performance_2016_1_DepDel15, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.Div1AirportID = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Code IS NOT NULL GROUP BY On_Time_On_Time_Performance_2016_1.Div1TailNum, On_Time_On_Time_Performance_2016_1.OriginCityName, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 90