SELECT On_Time_On_Time_Performance_2016_1.DepTime, L_AIRPORT_SEQ_ID.Description, MIN(On_Time_On_Time_Performance_2016_1.Div2AirportSeqID) AS min_On_Time_On_Time_Performance_2016_1_Div2AirportSeqID, SUM(On_Time_On_Time_Performance_2016_1.Cancelled) AS sum_On_Time_On_Time_Performance_2016_1_Cancelled, MAX(On_Time_On_Time_Performance_2016_1.FlightNum) AS max_On_Time_On_Time_Performance_2016_1_FlightNum, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID LEFT JOIN On_Time_On_Time_Performance_2016_1 ON L_AIRPORT_SEQ_ID.Code = On_Time_On_Time_Performance_2016_1.Month WHERE (L_AIRPORT_SEQ_ID.Code < 10000000 OR L_AIRPORT_SEQ_ID.Code IS NULL) GROUP BY On_Time_On_Time_Performance_2016_1.DepTime, L_AIRPORT_SEQ_ID.Description