SELECT L_AIRPORT_SEQ_ID.Description, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, MIN(L_AIRPORT_SEQ_ID.Code) AS min_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code FROM L_AIRPORT_SEQ_ID WHERE L_AIRPORT_SEQ_ID.Description != '' GROUP BY L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 78