SELECT On_Time_On_Time_Performance_2016_1.Div2Airport, L_AIRPORT_SEQ_ID.Description, MIN(On_Time_On_Time_Performance_2016_1.Distance) AS min_On_Time_On_Time_Performance_2016_1_Distance, MIN(On_Time_On_Time_Performance_2016_1.CRSArrTime) AS min_On_Time_On_Time_Performance_2016_1_CRSArrTime, STDDEV(On_Time_On_Time_Performance_2016_1.DepDel15) AS stddev_On_Time_On_Time_Performance_2016_1_DepDel15, COUNT(L_AIRPORT_SEQ_ID.Code) AS count_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, SUM(L_AIRPORT_SEQ_ID.Code) AS sum_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.DistanceGroup = L_AIRPORT_SEQ_ID.Code WHERE L_AIRPORT_SEQ_ID.Description != '' GROUP BY On_Time_On_Time_Performance_2016_1.Div2Airport, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 62