SELECT On_Time_On_Time_Performance_2016_1.DestStateName, L_AIRPORT_SEQ_ID.Description, AVG(On_Time_On_Time_Performance_2016_1.DayOfWeek) AS avg_On_Time_On_Time_Performance_2016_1_DayOfWeek, COUNT(On_Time_On_Time_Performance_2016_1.DistanceGroup) AS count_On_Time_On_Time_Performance_2016_1_DistanceGroup, AVG(On_Time_On_Time_Performance_2016_1.Div1TotalGTime) AS avg_On_Time_On_Time_Performance_2016_1_Div1TotalGTime, AVG(L_AIRPORT_SEQ_ID.Code) AS avg_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, STDDEV(L_AIRPORT_SEQ_ID.Code) AS stddev_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code, MAX(L_AIRPORT_SEQ_ID.Code) AS max_L_AIRPORT_SEQ_ID_Code FROM On_Time_On_Time_Performance_2016_1 INNER JOIN L_AIRPORT_SEQ_ID ON On_Time_On_Time_Performance_2016_1.OriginAirportID = L_AIRPORT_SEQ_ID.Code GROUP BY On_Time_On_Time_Performance_2016_1.DestStateName, L_AIRPORT_SEQ_ID.Description HAVING COUNT(*) > 85